mapper.select:
select count(userId)
from members
where userId = #{userId}

select userId, userName, userPassword, userEmail, userPhone, userRole, delflag, token
from members
where userId = #{userId}

select userName, userId, userEmail
from members 
where userId = #{userId}

select token
from members
where userId = #{userId}

mapper.update:
update members
set token = #{token}
where userId = #{userId}

update members
set token = #{token, jdbcType=VARCHAR}
where userId = #{userId}

update members

	userName = #{userName},
	userPassword = #{userPassword},
	userEmail= #{userEmail},
	userPhone = #{userPhone}

where userId = #{userId}
